mapper.select:
          
        select * from(
   	select rownum as rn, itemid,itemcode, itemname, itemprice, iteminfo, regDate from shop_item
	where
        

		

			itemName like '%' || #{keyword} || '%' and

		

        
            rownum <= #{pageNum}*#{amount} 
  
		)
  
        where rn > (#{pageNum} - 1) * #{amount}
